import React, { Component } from 'react';
import Navbar from './components/Navbar/Navbar';
import Jumbotron from './components/Jumbotron';
import Card from './components/Main/Card';
import characters from "./characters.json";
// import './App.css';

class App extends Component {
  // Initial state
  state = {
    score: 0,
    topScore: 0,
    characters
  }

  //When the character is clicked, the value of 'clicked' property is set to 'true'
  charactersClicked = clicked => {
    if (clicked === false) {
      alert(`You have clicked ${characters.name}`)
    }
    // const characters = this.state.characters.filter(character => character.clicked !== true);
    // // Set new state
    // this.setState({characters});
  }

  render() {
    return (
      <div>
        <Navbar/>
        <Jumbotron/>
        <main role="main" className="container">
          <div className="row">
            {this.state.characters.map(character => (
              <Card
              id={character.id}
              key={character.id}
              name={character.name}
              image={character.image}
              occupation={character.occupation}
              location={character.location}
              charactersClicked={this.charactersClicked}
              />
            ))}
          </div>

        </main>
      </div>
    );
  }
}

export default App;
